useeffect(() => {
	const unsubscribe = auth.onauthstatechanged((user) => {
		if (user) {
			setuniqueid(user.uid);
			setauthenticatedstate("authenticated");
		} else {
			setcurrentuser(null);
			setuniqueid(null);
			setauthenticatedstate("not-authenticated");
		}
	});
	return unsubscribe;
}, []);

// useeffect(() => {
	// checkuser();
